mapper.insert:
INSERT INTO COMMON_CODE_DETAIL ( 
	MASTER_ID
	,	PARENT_ID
	,	DETAIL_ID
	,	NAME
) 
VALUES (
	#{master_id} 
	,	#{parent_id}
	,	#{detail_id}
	,	#{name}
);

mapper.select:
SELECT DETAIL_ID FROM COMMON_CODE_DETAIL WHERE MASTER_ID = #{master_id}
AND PARENT_ID = #{parent_id}
ORDER BY DETAIL_ID DESC LIMIT 1
