mapper.insert:
INSERT INTO SUN_JOB ( 
	CATEGORY_ID
	,	CATEGORY_DETAIL_ID
	,	OCCUPATION
	,	NAME
	,	DEFINITION
	,	DESCRIPTION
	,	EDUCATION
	,	MAJOR
	,	LICENSE_ID
	, 	CAREER
	,	DIFFICULTY
	,	KNOWLEDGE
	,	SKILL
	,	ATTITUDE
	,	SALARY_LEVEL
	,	PROSPECT
	,	RANKING
	,	RELATED_JOB
	,	RELATED_OCCUPATION
	,	KECO
	,	COMPANY_1
	,	COMPANY_2
	,	COMPANY_3
	,	FUTURE_CAREER_1
	,	FUTURE_CAREER_2
	,	FUTURE_CAREER_3
	,	UPLOAD_FILE_ID

) 
VALUES (
	#{category_id} 
	,	#{category_detail_id}
	,	#{occupation}
	,	#{name}
	,	#{definition}
	,	#{description}
	,	#{education}
	,	#{major}
	,	#{license_id}
	,	#{career}
	,	#{difficulty}
	,	#{knowledge}
	,	#{skill}			
	,	#{attitude}
	,	#{salary_level}
	,	#{prospect}
	,	#{ranking}
	,	#{related_job}
	,	#{related_occupation}
	,	#{keco}
	,	#{company_1}
	,	#{company_2}
	,	#{company_3}
	,	#{future_career_1}
	,	#{future_career_2}
	,	#{future_career_3}
	,	#{upload_file_id}
);

INSERT INTO SUN_JOB_RELATIVE ( 
	RELATIVE_MASTER_ID
	,	RELATIVE_DETAIL_ID
	,	JOB_ID
) 
VALUES (
	#{relative_master_id} 
	,	#{relative_detail_id}
	,	#{job_id}
);

mapper.update:
UPDATE SUN_JOB SET   
	CATEGORY_ID = #{category_id}
	,	CATEGORY_DETAIL_ID = #{category_detail_id}
	,	OCCUPATION = #{occupation}
	,	NAME = #{name}
	,	DEFINITION = #{definition}
	,	DESCRIPTION = #{description}
	,	EDUCATION = #{education}
	,	MAJOR = #{major}
	,	LICENSE_ID = #{license_id}
	,	CAREER = #{career}
	,	DIFFICULTY = #{difficulty}
	,	KNOWLEDGE = #{knowledge}
	,	SKILL = #{skill}
	,	ATTITUDE = #{attitude}
	,	SALARY_LEVEL = #{salary_level}
	,	PROSPECT = #{prospect}
	,	RANKING = #{ranking}
	,	RELATED_JOB = #{related_job}
	,	RELATED_OCCUPATION = #{related_occupation}
	,	KECO = #{keco}
	,	COMPANY_1 = #{company_1}
	,	COMPANY_2 = #{company_2}
	,	COMPANY_3 = #{company_3}
	,	FUTURE_CAREER_1 = #{future_career_1}
	,	FUTURE_CAREER_2 = #{future_career_2}
	,	FUTURE_CAREER_3 = #{future_career_3}

		,	UPLOAD_FILE_ID = #{upload_file_id}

WHERE JOB_ID = #{job_id}

mapper.select:
SELECT JOB_ID , CATEGORY_ID , (SELECT NAME FROM COMMON_CODE_DETAIL WHERE MASTER_ID = 'M000002' AND MENU_ID = 'M000004' AND DETAIL_ID = A.CATEGORY_ID) AS CATEGORY_NAME , CATEGORY_DETAIL_ID , (SELECT NAME FROM COMMON_CODE_DETAIL WHERE MASTER_ID = 'M000003' AND MENU_ID = 'M000004' AND DETAIL_ID = A.CATEGORY_DETAIL_ID) AS CATEGORY_DETAIL_NAME , OCCUPATION , (SELECT NAME FROM COMMON_CODE_DETAIL WHERE MASTER_ID = 'M000004' AND DETAIL_ID = A.OCCUPATION) AS OCCUPATION_NAME , NAME , DEFINITION , DESCRIPTION , EDUCATION , (SELECT NAME FROM COMMON_CODE_DETAIL WHERE MASTER_ID = 'M000005' AND DETAIL_ID = A.EDUCATION) AS EDUCATION_NAME , CAREER , DIFFICULTY , (SELECT NAME FROM COMMON_CODE_DETAIL WHERE MASTER_ID = 'M000012' AND DETAIL_ID = A.DIFFICULTY) AS DIFFICULTY_NAME , KNOWLEDGE , SKILL , ATTITUDE , SALARY_LEVEL , PROSPECT , RANKING , RELATED_JOB , RELATED_OCCUPATION , KECO , COMPANY_1 , IFNULL((SELECT NAME FROM SUN_COMPANY B WHERE B.COMPANY_ID = A.COMPANY_1),"") AS COMPANY_1_NAME , COMPANY_2 , IFNULL((SELECT NAME FROM SUN_COMPANY B WHERE B.COMPANY_ID = A.COMPANY_2),"") AS COMPANY_2_NAME , COMPANY_3 , IFNULL((SELECT NAME FROM SUN_COMPANY B WHERE B.COMPANY_ID = A.COMPANY_3),"") AS COMPANY_3_NAME , FUTURE_CAREER_1 , IFNULL((SELECT NAME FROM SUN_JOB B WHERE B.JOB_ID = A.FUTURE_CAREER_1),"") AS FUTURE_CAREER_1_NAME , FUTURE_CAREER_2 , IFNULL((SELECT NAME FROM SUN_JOB B WHERE B.JOB_ID = A.FUTURE_CAREER_2),"") AS FUTURE_CAREER_2_NAME , FUTURE_CAREER_3 , IFNULL((SELECT NAME FROM SUN_JOB B WHERE B.JOB_ID = A.FUTURE_CAREER_3),"") AS FUTURE_CAREER_3_NAME , UPLOAD_FILE_ID , IFNULL((SELECT NAME FROM SUN_UPLOAD_FILE WHERE FILE_ID = A.UPLOAD_FILE_ID),"") AS UPLOAD_FILE_NAME FROM SUN_JOB A WHERE 1 = 1 AND USE_YN = 'Y'
AND CATEGORY_ID = #{category_id}
AND CATEGORY_DETAIL_ID = #{category_detail_id}
AND OCCUPATION = #{occupation}
AND EDUCATION = #{education}
AND DIFFICULTY = #{difficulty}
AND NAME LIKE CONCAT('%',#{name},'%')
AND JOB_ID IN ( (SELECT JOB_ID FROM SUN_JOB_RELATIVE WHERE RELATIVE_MASTER_ID = 'M000006' AND RELATIVE_DETAIL_ID = #{major}) )

SELECT JOB_ID , CATEGORY_ID , (SELECT NAME FROM COMMON_CODE_DETAIL WHERE MASTER_ID = 'M000002' AND MENU_ID = 'M000003' AND DETAIL_ID = A.CATEGORY_ID) AS CATEGORY_NAME , CATEGORY_DETAIL_ID , (SELECT NAME FROM COMMON_CODE_DETAIL WHERE MASTER_ID = 'M000003' AND MENU_ID = 'M000003' AND DETAIL_ID = A.CATEGORY_DETAIL_ID) AS CATEGORY_DETAIL_NAME , OCCUPATION , (SELECT NAME FROM COMMON_CODE_DETAIL WHERE MASTER_ID = 'M000004' AND DETAIL_ID = A.OCCUPATION) AS OCCUPATION_NAME , NAME , DEFINITION , DESCRIPTION , EDUCATION , (SELECT NAME FROM COMMON_CODE_DETAIL WHERE MASTER_ID = 'M000005' AND DETAIL_ID = A.EDUCATION) AS EDUCATION_NAME , CAREER , DIFFICULTY , (SELECT NAME FROM COMMON_CODE_DETAIL WHERE MASTER_ID = 'M000012' AND DETAIL_ID = A.DIFFICULTY) AS DIFFICULTY_NAME , KNOWLEDGE , SKILL , ATTITUDE , SALARY_LEVEL , PROSPECT , RANKING , RELATED_JOB , RELATED_OCCUPATION , KECO , COMPANY_1 , IFNULL((SELECT NAME FROM ESS_COMPANY B WHERE B.COMPANY_ID = A.COMPANY_1),"") AS COMPANY_1_NAME , COMPANY_2 , IFNULL((SELECT NAME FROM ESS_COMPANY B WHERE B.COMPANY_ID = A.COMPANY_2),"") AS COMPANY_2_NAME , COMPANY_3 , IFNULL((SELECT NAME FROM ESS_COMPANY B WHERE B.COMPANY_ID = A.COMPANY_3),"") AS COMPANY_3_NAME , FUTURE_CAREER_1 , IFNULL((SELECT NAME FROM ESS_JOB B WHERE B.JOB_ID = A.FUTURE_CAREER_1),"") AS FUTURE_CAREER_1_NAME , FUTURE_CAREER_2 , IFNULL((SELECT NAME FROM ESS_JOB B WHERE B.JOB_ID = A.FUTURE_CAREER_2),"") AS FUTURE_CAREER_2_NAME , FUTURE_CAREER_3 , IFNULL((SELECT NAME FROM ESS_JOB B WHERE B.JOB_ID = A.FUTURE_CAREER_3),"") AS FUTURE_CAREER_3_NAME , UPLOAD_FILE_ID , IFNULL((SELECT NAME FROM ESS_UPLOAD_FILE WHERE FILE_ID = A.UPLOAD_FILE_ID),"") AS UPLOAD_FILE_NAME FROM ESS_JOB A WHERE 1 = 1 AND USE_YN = 'Y'
AND CATEGORY_ID = #{category_id}
AND CATEGORY_DETAIL_ID = #{category_detail_id}
AND OCCUPATION = #{occupation}
AND EDUCATION = #{education}
AND DIFFICULTY = #{difficulty}
AND NAME LIKE CONCAT('%',#{name},'%')
AND JOB_ID IN ( (SELECT JOB_ID FROM ESS_JOB_RELATIVE WHERE RELATIVE_MASTER_ID = 'M000006' AND RELATIVE_DETAIL_ID = #{major}) )

