mapper.insert:
INSERT INTO  LICENSE ( 
	FIELD
	,	KOR_NAME
	,	ENG_NAME
	,	MINISTRY
	,	ENFORCEMENT_AGENCY
	,	SUMMARY
	,	CHANGE_PROCESS
	,	PERFORMANCE_JOB
	, 	PROSPECT
	, JOB_GB
) 
VALUES (
	#{field} 
	,	#{kor_name}
	,	#{eng_name}
	,	#{ministry}
	,	#{enforcement_agency}
	,	#{summary}
	,	#{change_process}
	,	#{performance_job}
	,	#{prospect}
	, #{job_gb}
);

mapper.select:
SELECT LICENSE_ID , FIELD , (SELECT NAME FROM COMMON_CODE_DETAIL WHERE MASTER_ID = 'M000007' AND DETAIL_ID = A.FIELD) AS FIELD_NAME , KOR_NAME , ENG_NAME , MINISTRY , ENFORCEMENT_AGENCY , SUMMARY , CHANGE_PROCESS , PERFORMANCE_JOB , PROSPECT , DATE_FORMAT(REG_DATE,'%Y-%m-%d') AS REG_DATE FROM LICENSE A WHERE 1 = 1 AND USE_YN = 'Y'
AND FIELD = #{field}
AND ( KOR_NAME LIKE CONCAT('%',#{name},'%') OR ENG_NAME LIKE CONCAT('%',#{name},'%') )
AND JOB_GB = #{job_gb}

SELECT LICENSE_ID , FIELD , (SELECT NAME FROM COMMON_CODE_DETAIL WHERE MASTER_ID = 'M000007' AND DETAIL_ID = A.FIELD) AS FIELD_NAME , KOR_NAME , ENG_NAME , MINISTRY , ENFORCEMENT_AGENCY , SUMMARY , CHANGE_PROCESS , PERFORMANCE_JOB , PROSPECT , DATE_FORMAT(REG_DATE,'%Y-%m-%d') AS REG_DATE FROM LICENSE A WHERE 1 = 1 AND USE_YN = 'Y'
AND JOB_GB = #{job_gb}
AND FIELD = #{field}
AND ( KOR_NAME LIKE CONCAT('%',#{name},'%') OR ENG_NAME LIKE CONCAT('%',#{name},'%') )

SELECT LICENSE_ID , FIELD , (SELECT NAME FROM COMMON_CODE_DETAIL WHERE MASTER_ID = 'M000007' AND DETAIL_ID = A.FIELD) AS FIELD_NAME , KOR_NAME , ENG_NAME , MINISTRY , ENFORCEMENT_AGENCY , SUMMARY , CHANGE_PROCESS , PERFORMANCE_JOB , PROSPECT , DATE_FORMAT(REG_DATE,'%Y-%m-%d') AS REG_DATE FROM LICENSE A WHERE USE_YN ='Y'
AND JOB_GB = #{job_gb}
AND FIELD = #{field}
AND ( KOR_NAME LIKE CONCAT('%',#{name},'%') OR ENG_NAME LIKE CONCAT('%',#{name},'%') )
AND FIELD IN( SELECT * FROM ( SELECT FIELD FROM LICENSE WHERE USE_YN ='Y'
AND JOB_GB = #{job_gb}
AND FIELD = #{field}
AND ( KOR_NAME LIKE CONCAT('%',#{name},'%') OR ENG_NAME LIKE CONCAT('%',#{name},'%') )
GROUP BY FIELD LIMIT #{start}, #{end} ) AS TMP )

mapper.update:
UPDATE LICENSE SET   
	FIELD = #{field}
	,	KOR_NAME = #{kor_name}
	,	ENG_NAME = #{eng_name}
	,	MINISTRY = #{ministry}
	,	ENFORCEMENT_AGENCY = #{enforcement_agency}
	,	SUMMARY = #{summary}
	,	CHANGE_PROCESS = #{change_process}
	,	PERFORMANCE_JOB = #{performance_job}
	,	PROSPECT = #{prospect}
	,	REG_DATE = NOW()
WHERE 
	LICENSE_ID = #{license_id}

mapper.select:
SELECT COUNT(*) FROM LICENSE A WHERE USE_YN ='Y'
AND FIELD = #{field}
AND ( KOR_NAME LIKE CONCAT('%',#{name},'%') OR ENG_NAME LIKE CONCAT('%',#{name},'%') )

SELECT COUNT(DISTINCT FIELD) FROM LICENSE A WHERE USE_YN ='Y'
AND FIELD = #{field}
AND ( KOR_NAME LIKE CONCAT('%',#{name},'%') OR ENG_NAME LIKE CONCAT('%',#{name},'%') )
