mapper.insert:
INSERT INTO TREND ( 
	WRITER
	,	TITLE
	,	EXPLANATION
	,	UPLOAD_FILE_ID
) 
VALUES (
	#{writer} 
	,	#{title}
	,	#{explanation}
	,	#{upload_file_id}
);

mapper.update:
UPDATE TREND SET   
	TITLE = #{title}
	,	WRITER = #{writer}
	,	EXPLANATION = #{explanation}

		,	UPLOAD_FILE_ID = #{upload_file_id}

	,	REG_DATE = NOW()
WHERE 
	TREND_ID = #{trend_id}

UPDATE TREND SET   
	HITS = HITS+1
WHERE TREND_ID = #{trend_id}
